import React, { useState } from "react";

function App() {
  const [textValue, setTextValue] = useState("");
  // const [topFive, setTopFive] = useState([])

  const count = () => {
    const string = textValue;

    //we need to take care of what to display first
    const seen = new Set();
    let order = "";

    //we took care of the count
    const count = {};

    for (let i = 0; i < string.length; i++) {
      let char = string[i];

      //keep track of count here
      count[char] = count[char] === undefined ? 0 : count[char] + 1;

      //keep track of order here
      if (!seen.has(char)) {
        order += char;
        seen.add(char);
      }
    }

    console.log(count);

    // console.log(count);
    return count;
  };
