// import "./App.css";

import React, { useState } from "react";

function App() {
  const [textValue, setTextValue] = useState("");
  const [characters, setCharacters] = useState([]);
  // const [topFive, setTopFive] = useState([])

  // abd3fssef
  // the count of character
  // when it first occurs

  const count = () => {
    const string = textValue;

    //we need to take care of what to display first
    const seen = new Set();
    let order = "";

    //we took care of the count
    const count = {};

    for (let i = 0; i < string.length; i++) {
      let char = string[i];

      //keep track of count here
      count[char] = count[char] === undefined ? 1 : count[char] + 1;

      //keep track of order here
      if (!seen.has(char)) {
        order += char;
        seen.add(char);
      }
    }

    // aaf2fk
    // order -> af2k
    /*
     * 2 - 1
     * a - 2
     * f - 2
     * k - 1
     */

    let out = [];
    for (let i = 0; i < order.length; i++) {
      let char = order[i];
      let countOfChar = count[char];
      out.push({
        character: char,
        count: countOfChar,
      });
    }
    setCharacters(out);

    let sortedArray = [];
    for (let char in count) {
      sortedArray.push([char, count[char]]);
    }
    sortedArray.sort((a, b) => a[1] - b[1]);
    //array is now sorted
    //grab the 5 top indexes
  };

  // const topFiveChars = () => {
  //   const topFive = []

  //   count
  // };

  return (
    <div>
      <div className="pt-10 pb-10">
        <div className="text-center">
          <p className="text-2xl font-bold text-red-600">TOP SECRET</p>
          <p className="text-xl font-bold p-2">Character Counter</p>
        </div>
      </div>
      <div className="flex justify-center">
        <textarea
          rows={"10"}
          style={{
            width: "50%",
            height: "50%",
            borderColor: "black",
            borderWidth: "3px",
            outline: "none",
            overflow: "auto",
          }}
          autoFocus
          placeholder="Enter Text Here"
          value={textValue}
          onChange={(e) => {
            setTextValue(e.target.value);
            count();
          }}
        ></textarea>
      </div>
      {/* <div className="flex justify-center p-10">
        <button
          className="bg-red-700 hover:bg-red-800 text-white font-bold py-2 px-4 rounded"
          onClick={count}
        >
          Evalute
        </button>
      </div> */}
      <div className="text-center">
        <p className="text-xl font-bold pb-10">
          Top 5 Most Frequent Charaters:
        </p>
      </div>
      <div className="flex justify-center space-x-10">
        {characters.map((character) => {
          return (
            <div>
              {character.character}:{character.count}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
